import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
var bg = { "background-color": "#99aab5" }

function Profile() {
  const [users, setUser] = useState({
    fname: "",
    email: "",
    hobbies: [],
  });

  const navigate = useNavigate();

  const { fname, email, hobbies } = users;

  const [selectedValues, setSelectedValues] = useState([]);

  const handleChange = (e) => {
    console.log("ujash 24", e);
    setUser({ ...users, [e.target.name]: e.target.value });
    console.log({ ...users, [e.target.name]: e.target.value });

  };


  const checkboxValue = (e) => {
    const { value, checked } = e.target;
    console.log(`${value} is ${checked}`,"maysl 33");

    if (checked) {
      // setUser([...users, value]);
      setUser({ ...users, [e.target.name]: e.target.value });
    } else {
      let finalValue = users && users.filter((e) => e !== [value]);
      setUser({ ...users, [e.target.name]: finalValue });
      // setUser(user && user.filter((e) => e !== value));
    }
  };




  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // user.hobbies = selectedValues
      // console.log(selectedValues)
      const res = await axios.put('http://localhost:4000/Route/index/users/update', users);
      console.log(res);
      localStorage.setItem('user-info', JSON.stringify(users));
      navigate('/dashboard');
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:4000/Route/index/profile');
        console.log(response,"60")
        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }, []);

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("user-info"));
    if (userInfo) {
      navigate("/profile");
    }
  }, [navigate]);


  return (

    <>
      <Header />
      <div className="profile-container">
        <Form onSubmit={handleSubmit}>

          <h1>Profile Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="fname" id="fname" value={users.fname} onChange={handleChange} placeholder="Enter Your Name" />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email" value={users.email} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>

          {/* <div className="form-check">
                          <input
