import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
var bg = { "background-color": "#99aab5" }

function Feedback() {
  const [user, setUser] = useState({
    fname: "",
    email: "",
    hobbies: [],
  });

  const navigate = useNavigate();

  // const { fname, email, hobbies } = user;

  const [selectedValues, setSelectedValues] = useState([]);

  const handleChange = (event) => {
    console.log("ujash 24", event);
    setUser({ ...user, [event.target.name]: event.target.value });
    console.log({ ...user, [event.target.name]: event.target.value });

  };


  // const checkboxValue = () => {
  //   setIsChecked(!isChecked);
  // };

  const [hobbies, setAgreement] = useState(false);

  const checkboxValue = (e) => {
    const { value, checked } = e.target;
    setAgreement(e.target.checked);

    console.log(`${value} is ${checked}`);

    if (checked) {
      setSelectedValues([...selectedValues, value]);
    } else {
      setSelectedValues(selectedValues && selectedValues.filter((e) => e !== value));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      user.hobbies = selectedValues
      console.log(selectedValues)
      const res = await axios.put('http://localhost:4000/Route/index/users/update', user);
      console.log(res);
      localStorage.setItem('user-info', JSON.stringify(user));
      navigate('/dashboard');
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:4000/Route/index/profile/join');
        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }, []);

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("user-info"));
    if (userInfo) {
      navigate("/profile");
    }
  }, [navigate]);


  return (

    <>
      <Header />
      <div className="profile-container">
        <Form onSubmit={handleSubmit}>

          <h1>Feedback Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="fname" id="fname" value={fname} onChange={handleChange} placeholder="Enter Your Name" />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email" value={email} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>
          <FormGroup>
            <Label for="mobile">Mobile_No</Label>
            <Input type="number" name="mobile" id="mobile" value={mobile} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>
          <FormGroup>
            <Label for="message">Message</Label>
            <Input type="textarea" name="Message" id="Message" value={Message} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>

          <Button style={bg}> Send</Button>
        </Form>
      </div>
    </>
  );
}

export default Feedback;
