import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
var bg = { "background-color": "#99aab5" }

const Feedback = () => {
    const [user, setUser] = useState({
        fname: "",
        email: "",
        mobile: "",
        Message: ""
      });
      const navigate = useNavigate();

      const { fname, email, mobile,Message } = user;


  const handleChange = (e) => {
    console.log("ujash 24", e);
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });

  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4000/Route/index/users/feedback', user);
      console.log(res);
    } catch (error) {
      console.log(error);
    }
  };




//   useEffect(() => {
//     const userInfo = JSON.parse(localStorage.getItem("user-info"));
//     if (userInfo) {
//       navigate("/profile");
//     }
//   }, [navigate]);



  return (

    <>

      <Header />
      <div className="profile-container">
        <Form
         onSubmit={handleSubmit}
         >

          <h1>Feedback Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="fname" id="fname"
             value={fname} onChange={handleChange} 
             placeholder="Enter Your Name" />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email"
             value={email} onChange={handleChange} 
             placeholder="Enter Your Email" />
          </FormGroup>
          <FormGroup>
            <Label for="Mobile">Mobile</Label>
            <Input type="number" name="mobile" id="mobile"
             value={mobile} onChange={handleChange} 
             placeholder="Enter Your Mobile" />
          </FormGroup>
          <FormGroup>

            <Label for="Message">Message</Label>
            <Input type="textarea" name="Message" id="Message"
             value={Message} onChange={handleChange} 
             placeholder="Enter Your Message" />

          </FormGroup>

          <Button style={bg}> Profile Save</Button>
        </Form>
      </div>
    </>
  )
}

export default Feedback
