import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
import axios from "axios";


const [user, setUser] = useState({
  name: "",
  email: "",
  hobbies: "",
});
const navigate = useNavigate();
const { name,email, hobbies } = user;

const handleChange = (e) => {
  setUser({ ...user, [e.target.name]: e.target.value });
  console.log({ ...user, [e.target.name]: e.target.value });
};


const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await axios.post('http://localhost:4000/Route/index/profile/update', user);
    console.log(res);
    localStorage.setItem('user-info', JSON.stringify(user));
  //  navigate('/dashboard');
  } catch (error) {
    console.log(error);
  }

};
useEffect(() => {
  const userInfo = JSON.parse(localStorage.getItem("user-info"));
  if (userInfo) {
    navigate("/");
  }
}, [navigate]);

function Profile() {
  return (
    <div className="profile-container">
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="name">FName</Label>
          <Input type="text" name="name" id="name" value={name}  onChange={handleChange} placeholder="Enter your name" />
        </FormGroup>
        <FormGroup>
          <Label for="email">Email</Label>
          <Input type="email" name="email" id="email" value={email}  onChange={handleChange} placeholder="Enter your email" />
        </FormGroup>

        <FormGroup>
          <Label for="interests">Hobbies</Label>
          <div>
            <FormGroup check inline>
              <Label check>
                <Input type="checkbox" name="hobbies" value="music" />{' '}
                Music
              </Label>
            </FormGroup>
            <FormGroup check inline>
              <Label check>
                <Input type="checkbox" name="hobbies" value="movies" />{' '}
                Movies
              </Label>
            </FormGroup>
            <FormGroup check inline>
              <Label check>
                <Input type="checkbox" name="hobbies" value="sports" />{' '}
                Sports
              </Label>
            </FormGroup>
          </div>
        </FormGroup>
        <Button color="primary"> Profile Save</Button>
      </Form>
    </div>
  );
}

export default Profile;
