import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
var bg = { "background-color": "#99aab5" }

function Profile() {
  const [user, setUser] = useState({
    fname: "",
    email: "",
    hobbies: [],
  });

  const navigate = useNavigate();

  const { fname, email, hobbies } = user;

  const [selectedValues, setSelectedValues] = useState([]);

  const handleChange = (e) => {
    console.log("ujash 24", e);
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });

  };


  const checkboxValue = (e) => {
    const { value, checked } = e.target;
    console.log(`${value} is ${checked}`);

    if (checked) {
      setSelectedValues([...selectedValues, value]);
    } else {
      setSelectedValues(selectedValues && selectedValues.filter((e) => e !== value));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      user.hobbies = selectedValues
      console.log(selectedValues)
      const res = await axios.put('http://localhost:4000/Route/index/users/update', user);
      console.log(res);
      localStorage.setItem('user-info', JSON.stringify(user));
      navigate('/dashboard');
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get('http://localhost:4000/Route/index/profile/join');
        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }, []);

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("user-info"));
    if (userInfo) {
      navigate("/profile");
    }
  }, [navigate]);


  return (

    <>
      <Header />
      <div className="profile-container">
        <Form onSubmit={handleSubmit}>

          <h1>Profile Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="fname" id="fname" value={user.fname} onChange={handleChange} placeholder="Enter Your Name" />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email" value={user.email} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>

          <div className="form-check">
                          <input
                          className="form-check-input"
                            type="checkbox"
                            value="Cricket"
                            id="cricket"
                            checked={user?.hobbies?.includes("Cricket")}
                            onChange={(e) => checkboxValue(e)}
                          />
                          <label className="form-check-label" htmlFor="Reading">
                            Cricket
                          </label>
                        </div>
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value="Reading"
                            checked={user?.hobbies?.includes("Reading")}
                            onChange={(e) => checkboxValue(e)}
                            id="Reading"
                          />
                          <label className="form-check-label" htmlFor="Reading">
                            Reading
                          </label>
                        </div>
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value="Traveling"
                            checked={user?.hobbies?.includes("Traveling")}
                            onChange={(e) => checkboxValue(e)}
                            id="Traveling"
                          />
                          <label className="form-check-label" htmlFor="Traveling">
                            Traveling
                          </label>
                        </div>


          <FormGroup>
            <Label for="hobbies">Hobbies</Label>
            <div>
              <FormGroup check inline>
                <Label checked>
                  <Input type="checkbox" name="hobbies" value="Music" onChange={checkboxValue}
                    checked=  { user?.hobbies?.includes("Music")}  
                  />
                  Music
                </Label>
              </FormGroup>

              <FormGroup check inline>
                <Label checked >
                  <Input type="checkbox" name="hobbies" value="Movies" onChange={checkboxValue}
                    checked=  { user?.hobbies?.includes("Movies")}     
                  />
                  Movies
                </Label>
              </FormGroup>

              <FormGroup check inline>
                <Label checked>
                  <Input type="checkbox" name="hobbies" value="Sports" onChange={checkboxValue}
                    checked=  { user?.hobbies?.includes("Sports")}     
                  />
                  Sports
                </Label>
              </FormGroup>

            </div>
          </FormGroup>
          <Button style={bg}> Profile Save</Button>
        </Form>
      </div>
    </>
  );
}

export default Profile;
