import React, { useState,useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
import axios from "axios";
import Header from './Header';




function Profile() {

  const [user, setUser] = useState({
    fname: "",
    email: "",
    hobbies: "",

  });
  const navigate = useNavigate();
  const {fname,email } = user;




  const handleChange = (e) => {
    console.log("ujash 24", e);
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });
  };


  const [selectedValues, setSelectedValues] = useState([]);

  function handleCheckboxChange(event) {
    const value = event.target.value;

    if (event.target.checked) {
      setSelectedValues([...selectedValues, value]);
    } else {
      setSelectedValues(selectedValues.filter((val) => val !== value));
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4000/Route/index/profile/create', user);
      console.log(res);
      localStorage.setItem('user-info', JSON.stringify(user));
     navigate('/dashboard');
    } catch (error) {
      console.log(error);
    }

  };

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("user-info"));
    if (userInfo) {
      navigate("/profile");
    }
  }, [navigate]);


  return (

   <>
    <Header/>
    <div className="profile-container">

      <Form onSubmit={handleSubmit}>

        <FormGroup>
          <Label for="name">FName</Label>
          <Input type="text" name="fname" id="fname" value={fname}  onChange={handleChange} placeholder="Enter your name" />
        </FormGroup>
        <FormGroup>
          <Label for="email">Email</Label>
          <Input type="email" name="email" id="email" value={email}  onChange={handleChange} placeholder="Enter your email" />
        </FormGroup>

        <FormGroup>
          <Label for="hobbies">Hobbies</Label>
          <div>
            <FormGroup check inline>
              <Label check>
                <Input type="checkbox" name="hobbies" value="Music"    onChange={handleCheckboxChange}  />{' '}
                Music
              </Label>
            </FormGroup>
            <FormGroup check inline>
              <Label check>
                <Input type="checkbox" name="hobbies" value="Movies"  onChange={handleCheckboxChange}  />{' '}
                Movies
              </Label>
            </FormGroup>
            <FormGroup check inline>
              <Label check>
                <Input type="checkbox" name="hobbies" value="Sports"    onChange={handleCheckboxChange} />{' '}
                Sports
              </Label>
            </FormGroup>
          </div>
        </FormGroup>
        <Button color="primary"> Profile Save</Button>
      </Form>
    </div>
    </>
  );
}

export default Profile;
