import 'reactjs-popup/dist/index.css';
import Popup from 'reactjs-popup';
import React, { useState } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
var bg = { "background-color": "#99aab5" }




const Feedback = () => {
    const [user, setUser] = useState({
        name: "",
        email: "",
        mobile: "",
        message: ""
      });
      const navigate = useNavigate();

      const { name, email, mobile,message } = user;


  const handleChange = (e) => {
    console.log("ujash 24", e);
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });

  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4000/Route/index/users/feedback', user);
      console.log(res);
      // navigate("/dashboard");
    } catch (error) {
      console.log(error);
    }
  };




//   useEffect(() => {
//     const userInfo = JSON.parse(localStorage.getItem("user-info"));
//     if (userInfo) {
//       navigate("/profile");
//     }
//   }, [navigate]);



  return (

    <>

      <Header />


      <div>

            <Popup trigger=
                {  <Button style={bg}> Feedback</Button>}
                position="right left">

      <div className="profile-container">
        <Form
         onSubmit={handleSubmit}
         >

          <h1>Feedback Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="name" id="name"
             value={name} onChange={handleChange} 
             placeholder="Enter Your Name" />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email"
             value={email} onChange={handleChange} 
             placeholder="Enter Your Email" />
          </FormGroup>
          <FormGroup>
            <Label for="mobile">Mobile</Label>
            <Input type="number" name="mobile" id="mobile"
             value={mobile} onChange={handleChange} 
             placeholder="Enter Your Mobile" />
          </FormGroup>
          <FormGroup>

            <Label for="message">Message</Label>
            <Input type="textarea" name="message" id="message"
             value={message} onChange={handleChange} 
             placeholder="Enter Your Message" />

          </FormGroup>

          <Button style={bg}> Profile Save</Button>
        </Form>
      </div>

            </Popup>
        </div>


    </>
  )
}

export default Feedback
