import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";
var bg = { "background-color": "#99aab5" }

const Feedback = () => {
  return (

    <>

      <Header />
      <div className="profile-container">
        <Form onSubmit={handleSubmit}>

          <h1>Profile Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="fname" id="fname" value={user.fname} onChange={handleChange} placeholder="Enter Your Name" />
          </FormGroup>

          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email" value={user.email} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>

          <FormGroup>
            <Label for="hobbies">Hobbies</Label>
            <div>
              <FormGroup check inline>
                <Label >
                  <Input type="checkbox" name="hobbies" value="Music" onChange={checkboxValue}
                    // checked=  { user?. hobbies?.includes("Music")}



                  />
                  Music
                </Label>
              </FormGroup>

              <FormGroup check inline>
                <Label check>
                  <Input type="checkbox" name="hobbies" value="Movies" onChange={checkboxValue}
                    // checked=  { user?. hobbies?.includes("Movies")}

                  />
                  Movies
                </Label>
              </FormGroup>

              <FormGroup check inline>
                <Label checked>
                  <Input type="checkbox" name="hobbies" value="Sports" onChange={checkboxValue}
                    // checked=  { user?. hobbies?.includes("Sports")}

                  />
                  Sports
                </Label>
              </FormGroup>

            </div>
          </FormGroup>
          <Button style={bg}> Profile Save</Button>
        </Form>
      </div>
    </>
  )
}

export default Feedback
