 import React, { useState, useEffect } from 'react'
 import Header from './Header'
 import axios from "axios";

 const List = () => {
   const [users, setUsers] = useState([]);
   const [currentPage, setCurrentPage] = useState(1);
   const [usersPerPage] = useState(5);
   const [seachData, setSeachData] = useState([]);

   const handleChange = (value) => { 
     const fname = seachData.filter(item => item.fname.toLowerCase() === value.toLowerCase(value))

   useEffect(() => {
     const fetchData = async () => {
       try {
         const res = await axios.post('http://localhost:4000/Route/index/users/listing', {
           page: currentPage,
           perPage: usersPerPage,
         });
         console.log(res.data);
         setUsers(res.data);
         setSeachData(res.data);
       } catch (error) {
         console.error(error);
       }
     }
     fetchData();
   }, [currentPage]);


   const indexOfLastUser = currentPage * usersPerPage;
   const indexOfFirstUser = indexOfLastUser - usersPerPage;
   const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);

   const totalPages = Math.ceil(users.length / usersPerPage);
   const pageNumbers = [];

   for (let i = 1; i <= totalPages; i++) {
     pageNumbers.push(i);
   }
   const goToNextPage = () => {
     setCurrentPage((prevPage) => prevPage + 1);
   };

   const goToPrevPage = () => {
     setCurrentPage((prevPage) => prevPage - 1);
   };

   return (
     <>
       <Header />

       <div className="container mt-5">

         <table class="table table-striped">
           <thead>
             <tr>
               <th scope="col">Id</th>
               <th scope="col">Name</th>
               <th scope="col">Email</th>
               <th scope="col">Hobbies</th>
             </tr>
           </thead>
           <tbody>
             {currentUsers.map((item, index) => {
               return (
                 <tr key={index}>
                   <td>{item.id}</td>
                   <td>{item.fname}</td>
                   <td>{item.email}</td>
                   <td>{JSON.parse(item.hobbies)}</td>
                 </tr>
               );
             })}

           </tbody>
         </table>


         <>

           <ul className="pagination">
             <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
               <button
                 className="page-link"
                 onClick={goToPrevPage}
                 disabled={currentPage === 1}
               >
                 Prev
               </button>
             </li>

             {pageNumbers.map((number) => (
               <li
                 key={number}
                 className={`page-item ${currentPage === number ? 'active' : ''}`}
               >
                 <button
                   className="page-link"
                   onClick={() => setCurrentPage(number)}
                 >
                   {number}
                 </button>
               </li>
             ))}

             <li
               className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}
             >
               <button
                 className="page-link"
                 onClick={goToNextPage}
                 disabled={currentPage === totalPages}
               >
                 Next
               </button>
             </li>
           </ul>
         </>

       </div>
     </>
   )
 }
};

 export default List;
