import React, { useState } from "react";
// import NavBar from "../components/NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Button from '../../node_modules/react-bootstrap/Button';
import Form from '../../node_modules/react-bootstrap/Form';
import Modal from '../../node_modules/react-bootstrap/Modal';
const FeedBack = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    mobile: "",
    message: ""
  });
  const navigate = useNavigate();

  const { name, email, mobile,message } = user;


  const handleChange = (e) => {
    console.log("ujash 24", e);
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });

  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4000/Route/index/users/feedback', user);
      console.log(res);
      // navigate("/dashboard");
    } catch (error) {
      console.log(error);
    }
  };


  // const [data, setData] = useState({
  //   name: "",
  //   email: "",
  //   mobile: "",
  //   messgae: "",
  // });
  // const { name, email, mobile, message } = data;
  // const navigate = useNavigate();
  // const handleChange = (e) => {
  //   setData({ ...data, [e.target.name]: e.target.value });
  // };
  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   try {
  //     const response = await axios.post(
  //       "http://localhost:8900/post/feedback",
  //       data
  //     );
  //     console.log(response);
  //     navigate("/dashboard");
  //   } catch (err) {
  //     console.log(err);
  //   }
  // };

  return (
    <>
      <div>


        <Button variant="success"  onSubmit={handleSubmit}>
        Feedback
        </Button>
      <Modal >
        <Modal.Header closeButton>
          <Modal.Title>Feedback Page</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="name" id="name"
                value={name} onChange={handleChange}
                placeholder="Enter Your Name"
                autoFocus
              />
               </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Email </Form.Label>
              <Form.Control
                type="email"
                name="email" id="email"
                value={email} onChange={handleChange}
                placeholder="name@example.com"
                autoFocus
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Mobile </Form.Label>
              <Form.Control
                type="number"
                name="mobile" id="mobile"
                value={mobile} onChange={handleChange}
                placeholder="Enter Your Mobile"
                autoFocus
              />
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Example textarea</Form.Label>
              <Form.Control as="textarea" 
              name="message" id="message"
              rows={3} 
              value={message} onChange={handleChange} 
              placeholder="Enter Your Message"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" >
            Close
          </Button>
          <Button variant="primary" >
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      </div>
    </>
  );
};
export default FeedBack;
