import React, { useState,useEffect} from 'react'
// import React,{ useEffect } from "react"; 
import bootstrap from '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import {useNavigate} from "react-router-dom"


import axios from "axios";  

import './Login.css';

function Login() {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  const navigate = useNavigate();
  const {username, email, password } = user;


  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4000/Route/index/users/loginapi', user);
      console.log(res);
      localStorage.setItem('user-info', JSON.stringify(user));
      navigate('/dashboard');
    } catch (error) {
      console.log(error);
    }

  };
  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("user-info"));
    if (userInfo) {
      navigate("/dashboard");
    }
  }, [navigate]);
  return (
<>
<section class="main vh-100">
  <div class="container-fluid h-custom">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-md-9 col-lg-6 col-xl-5">
        <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp"
          class="img-fluid" alt="Sample image"/>
      </div>
      <div class="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
      <form className="login-form" onSubmit={handleSubmit}>

      <div class="form-outline mb-4">
          <label class="form-label" for="form3Example3">Username </label>
            <input type="text" id="username"  name="username"  value={username}
    onChange={handleChange} class="form-control form-control-lg"
              placeholder="Enter a valid username" />

          </div>

          <div class="form-outline mb-4">
          <label class="form-label" for="form3Example3">Email / Username </label>
            <input type="email"  id="email"
    name="email"
    value={email}
    onChange={handleChange} class="form-control form-control-lg"
              placeholder="Enter a valid email address" />

          </div>


          <div class="form-outline mb-3">
          <label class="form-label" for="form3Example4">Password</label>
            <input type="password"  id="password"
    name="password"
    value={password}
    onChange={handleChange} class="form-control form-control-lg"
              placeholder="Enter password" />

          </div>

          <div class="d-flex justify-content-between align-items-center">

            <div class="form-check mb-0">
              <input class="form-check-input me-2" type="checkbox" value="" id="form2Example3" />
              <label class="form-check-label" for="form2Example3">
                Remember me
              </label>
            </div>

          </div>

          <div class="text-center text-lg-start mt-4 pt-2">
            <button type="submit" class="btn btn-primary btn-lg"
            >Submit</button>

          </div>

        </form>
      </div>
    </div>
  </div>

</section>
</> 
  )
}

export default Login;
