}
    return (
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            name='email'
            value={user.email}
            onChange={(e) => handleChange(e)}
            required
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            name='password'
            value={user.password}
            onChange={(e) => handleChange(e)}
            required
          />
        </label>
        <button type="submit">Login</button>
      </form>
    );
  }

export default Login;
