import React, { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom"
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import axios from "axios";
import Header from './Header';
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";


function Profile() {
  const [user, setUser] = useState({
    fname: "",
    email: "",
  });
  const navigate = useNavigate();
  const { fname, email } = user;

  const [selectedValues, setSelectedValues] = useState([]);


  const handleChange = (event) => {

    console.log("ujash 24", event);
    setUser({ ...user, [event.target.name]: event.target.value });
    console.log({ ...user, [event.target.name]: event.target.value });

  };

  const checkboxValue = (e) => {
    const { value, checked } = e.target;
    console.log(`${value} is ${checked}`);

    if (checked) {
      setSelectedValues([...selectedValues, value]);
    } else {
      setSelectedValues(selectedValues && selectedValues.filter((e) => e !== value));
    }
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      user.hobbies = selectedValues
      console.log(selectedValues)
      const res = await axios.post('http://localhost:4000/Route/index/profile/create', user);
      console.log(res);
      localStorage.setItem('user-info', JSON.stringify(user));
      navigate('/dashboard');
    } catch (error) {
      console.log(error);
    }

  };

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("user-info"));
    if (userInfo) {
      navigate("/profile");
    }
  }, [navigate]);


  return (

    <>
      <Header />
      <div className="profile-container">
        <Form onSubmit={handleSubmit}>

          <h1>Profile Page</h1>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" name="fname" id="fname" value={fname} onChange={handleChange} placeholder="Enter Your Name" />
          </FormGroup>
          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email" value={email} onChange={handleChange} placeholder="Enter Your Email" />
          </FormGroup>

          <FormGroup>
            <Label for="hobbies">Hobbies</Label>
            <div>
              <FormGroup check inline>
                <Label check>
                  <Input type="checkbox" name="hobbies" value="Music" onChange={checkboxValue} />{' '}
                  Music
                </Label>
              </FormGroup>

              <FormGroup check inline>
                <Label check>
                  <Input type="checkbox" name="hobbies" value="Movies" onChange={checkboxValue} />{' '}
                  Movies
                </Label>
              </FormGroup>
              <FormGroup check inline>
                <Label check>
                  <Input type="checkbox" name="hobbies" value="Sports" onChange={checkboxValue} />{' '}
                  Sports
                </Label>
              </FormGroup>
            </div>
          </FormGroup>
          <Button color="primary"> Profile Save</Button>
        </Form>
      </div>
    </>
  );
}

export default Profile;
