import React, { useState } from 'react'
// import React,{ useEffect } from "react"; 
import 'bootstrap/dist/css/bootstrap.min.css'
import { Link, useNavigate } from "react-router-dom"
import axios from "axios";
import './Register.css';

const Register = () => {
  const [user, setUser] = useState({
    fname: "",
    email: "",
    password: "",
  });
  const navigate = useNavigate();
  const { fname, email, password } = user;


  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios.post("http://localhost:4000/Route/index/users/loginpost", user)
      localStorage.setItem('user-info', JSON.stringify(user))
      if ("user-info") {
        navigate('/dashboard')
      }
      else {
        navigate('/')
      }
    }
    catch (err) {
      console.log(err)
    }
  }
  //   useEffect(() => {
  //     const userInfo = JSON.parse(localStorage.getItem("user-info"));
  //     if (userInfo) {
  //       navigate("/dashboard");
  //     }
  //   }, [navigate]);


  return (
    <>
      <section class="main vh-100">
        <div class="container-fluid h-custom">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-md-9 col-lg-6 col-xl-5">
              <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp"
                class="img-fluid" alt="Sample image" />
            </div>
            <div class="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
              <form className="login-form" onSubmit={handleSubmit}> 
                <div class="form-outline mb-4">
                  <label class="form-label" for="form3Example3">first name </label>
                  <input type="text" id="fname"
                    name="fname"
                    value={fname}
                    onChange={e => handleChange(e)} class="form-control form-control-lg" placeholder="Enter first name" />
                </div>
               <div class="form-outline mb-4">
                  <label class="form-label" for="form3Example3">email </label>
                  <input type="email" id="email"
                    name="email"
                    value={email}
                    onChange={e => handleChange(e)} class="form-control form-control-lg" placeholder="Enter a valid email / Username" />
                </div>
                <div class="form-outline mb-3">
                  <label class="form-label" for="form3Example4">Password</label>
                  <input type="password" id="password"
                    name="password"
                    value={password}
                    onChange={e => handleChange(e)} class="form-control form-control-lg"
                    placeholder="Enter password" />

                </div>
                <div class="text-center text-lg-start mt-4 pt-2">
                  <button type="submit" class="btn btn-primary btn-lg"
                  >Submit</button>
                  <p class="small fw-bold mt-2 pt-1 mb-0">Don't have an account? <Link to="/"
                    class="link-danger">Login</Link></p>

                </div>

              </form>
            </div>
          </div>
        </div>

      </section>
    </>
  )
}



export default Register
